import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer } from 'react-leaflet';
import { Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet-control-geocoder/dist/Control.Geocoder.css';
import 'leaflet-control-geocoder/dist/Control.Geocoder.js';
const mapRef = useRef(null); // Assuming you are using useRef for mapRef


const Map = ({ center, zoom }) => {
  const [covidData, setCovidData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://covid-19-tracking.p.rapidapi.com/v1', {
          method: 'GET',
          headers: {
            'X-RapidAPI-Key': '156cf06ba4msh2c2cdb55142a549p162ba6jsncff6594c9c0b',
            'X-RapidAPI-Host': 'covid-19-tracking.p.rapidapi.com',
          },
        });

        if (!response.ok) {
          throw new Error('Failed to fetch COVID-19 data');
        }

        const data = await response.json();
        setCovidData(data);
      } catch (error) {
        console.error('Error fetching COVID-19 data:', error.message);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    // Initialize the geocoder control
    const geocoder = L.Control.geocoder({
      defaultMarkGeocode: false,
    }).on('markgeocode', (e) => {
      const { properties } = e.geocode;
      console.log('Selected location:', properties);
    });

    // Add the geocoder control to the map
    geocoder.addTo(mapRef.current);
  }, []);

  return (
    <div className="map">
      <MapContainer
        center={center}
        zoom={zoom}
        style={{ height: '500px', width: '100%' }}
      >
        <TileLayer
          url="https://api.maptiler.com/maps/basic-v2/{z}/{x}/{y}.png?key=JdbYq0IOmnjd0Jgw8cH7"
          tileSize={512}
          zoomOffset={-1}
          attribution='<a href="https://www.maptiler.com/copyright/" target="_blank">&copy; MapTiler</a> <a href="https://www.openstreetmap.org/copyright" target="_blank">&copy; OpenStreetMap contributors</a>'
        />

        {/* Add markers for each country using the geocoder control */}
        {covidData.map((country) => (
          <Marker
            key={country.Country_text}
            position={country.Country_text}  
          >
            <Popup>
              <strong>{country.Country_text}</strong>
              <br />
              Total Cases: {country.TotalCases_text}
              <br />
              Total Deaths: {country.TotalDeaths_text}
              <br />
              Total Recovered: {country.TotalRecovered_text}
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

// Default Map props if none are specified
Map.defaultProps = {
  center: {
    lat: 37.0902,
    lng: -95.7129,
  },
  zoom: 4,
};

export default Map;
